INSERT INTO `pss_prod_stuff` (`id`, `id_folder`, `id_brand`, `name`, `headline`, `description`, `pic`, `best_type`, `new_type`, `price_1`, `price_2`, `discount`, `desc_feature`, `desc_spec`, `desc_box`, `meta_tag`, `meta_key`, `meta_desc`) VALUES
('20080919072053', '20080919070258', '20080919070002', 'Garmin GPS 60 i (Versi Indonesia)', 'Making it easy to navigate', 'Backwoods, boundary waters, blue highways - the GPS 60 is your trusted guide to the great outdoors. The GPS 60 and its counterpart, the GPSMAP 60, are packed with several of the features found in the 60-series color navigators, but sport an affordable, high-resolution monochrome display for outdoor enthusiasts on a budget.', '20080919072053-Basic Handheldx.jpg', 0, 0, '2500000', NULL, NULL, '<div id="desc1">\r <h3>Get Rugged Navigation</h3>\r <br /><br />The robust, waterproof GPS 60 is the perfect, low-cost navigator for wherever your spirit of adventure takes you:         \r <ul>\r <li>Large, sunlight-readable, four-level grayscale display with bright LED backlight makes it easy to find your way, day or night</li>\r <li>Up to 28 hours of battery life for more hours of outdoor enjoyment</li>\r <li><a href="http://www.garmin.com/aboutGPS/waas.html">WAAS</a>-enabled for even more precise location and navigation </li>\r <li>Both serial and USB connections for fast and versatile connection to your computer </li>\r </ul>\r <h3>Get Fun-filled Features</h3>\r <br /><br />GPS 60 has one megabyte (MB) of internal memory for storing points of interest, and it comes factory preloaded with a worldwide city point database. Just find the place you want to go to, and the GPS 60 creates a straight-line route to your destination. The GPS 60 also offers special geocaching features, indoor and outdoor games, an alarm clock, sunrise/sunset and moon phase tables, optimal hunting and fishing times &mdash; plus much more.  <br /><br />GPS 60: Making it easy to navigate the great outdoors on a budget.</div>', '<table id="playlist" border="0" cellspacing="0">\r <tbody>\r <tr class="title">\r <td colspan="2">\r <h3>Physical &amp; Performance:</h3>\r </td>\r </tr>\r <tr class="odd">\r <td>Unit dimensions, WxHxD:</td>\r <td>2.4" x 6.1" x 1.3" (6.1 x 15.5 x 3.3 cm)</td>\r </tr>\r <tr class="even">\r <td>Display size, WxH:</td>\r <td>1.5" x 2.2" (3.8 x 5.6 cm)</td>\r </tr>\r <tr class="odd">\r <td>Display resolution, WxH:</td>\r <td>160 x 240 pixels</td>\r </tr>\r <tr class="even">\r <td>Display type:</td>\r <td>4 level gray LCD</td>\r </tr>\r <tr class="odd">\r <td>Weight:</td>\r <td>7.0 oz (198 g) with batteries</td>\r </tr>\r <tr class="even">\r <td>Battery:</td>\r <td>2 AA batteries (not included)</td>\r </tr>\r <tr class="odd">\r <td>Battery life:</td>\r <td>up to 28 hours</td>\r </tr>\r <tr class="even">\r <td>Waterproof:</td>\r <td>yes (IPX7)</td>\r </tr>\r <tr class="odd">\r <td>Floats:</td>\r <td>no</td>\r </tr>\r <tr class="even">\r <td>High-sensitivity receiver:</td>\r <td>no</td>\r </tr>\r <tr class="odd">\r <td>Interface:</td>\r <td>serial and USB</td>\r </tr>\r <tr class="even">\r <td>RoHS version available:</td>\r <td>yes</td>\r </tr>\r </tbody>\r </table>\r <table id="playlist" border="0" cellspacing="0">\r <tbody>\r <tr class="title">\r <td colspan="2">\r <h3>Maps &amp; Memory:</h3>\r </td>\r </tr>\r <tr class="even">\r <td>Basemap:</td>\r <td>no</td>\r </tr>\r <tr class="odd">\r <td>Preloaded maps:</td>\r <td>no</td>\r </tr>\r <tr class="even">\r <td>Ability to add maps:</td>\r <td>accepts points of interest data</td>\r </tr>\r <tr class="odd">\r <td>Built-in memory:</td>\r <td>1 MB</td>\r </tr>\r <tr class="even">\r <td>Accepts data cards:</td>\r <td>no</td>\r </tr>\r <tr class="odd">\r <td>Waypoints/favorites/locations:</td>\r <td>500</td>\r </tr>\r <tr class="even">\r <td>Routes:</td>\r <td>50</td>\r </tr>\r <tr class="odd">\r <td>Track log:</td>\r <td>10,000 points, 20 saved tracks</td>\r </tr>\r </tbody>\r </table>\r <table id="playlist" border="0" cellspacing="0">\r <tbody>\r <tr class="title">\r <td colspan="2">\r <h3>Features:</h3>\r </td>\r </tr>\r <tr class="odd">\r <td>Automatic routing (turn by turn routing on roads):</td>\r <td>no</td>\r </tr>\r <tr class="even">\r <td>Electronic compass:</td>\r <td>no</td>\r </tr>\r <tr class="odd">\r <td>Barometric altimeter:</td>\r <td>no</td>\r </tr>\r <tr class="even">\r <td>Geocaching-friendly:</td>\r <td>yes</td>\r </tr>\r <tr class="odd">\r <td>Outdoor GPS games:</td>\r <td>yes</td>\r </tr>\r <tr class="even">\r <td>Hunt/fish calendar:</td>\r <td>yes</td>\r </tr>\r <tr class="odd">\r <td>Sun and moon information:</td>\r <td>yes</td>\r </tr>\r <tr class="even">\r <td>Tide tables:</td>\r <td>no</td>\r </tr>\r <tr class="odd">\r <td>Area calculation:</td>\r <td>yes</td>\r </tr>\r <tr class="even">\r <td>Custom POIs (ability to add additional points of interest):</td>\r <td>no</td>\r </tr>\r <tr class="odd">\r <td>Unit-to-unit transfer (shares data wirelessly with similar units):</td>\r <td>no</td>\r </tr>\r <tr class="even">\r <td>Picture viewer:</td>\r <td>no</td>\r </tr>\r </tbody>\r </table>', '<div id="desc3" style="display: block;">\r <h3>What&#39;s in the Box:</h3>\r <ul>\r <li>GPS 60</li>\r <li>Database: Worldwide City Point</li>\r <li>MapSource&reg; Trip &amp; Waypoint Manager CD</li>\r <li>USB cable</li>\r <li>Wrist strap</li>\r <li>Belt clip with button</li>\r <li>Owner&#39;s manual</li>\r <li>Quick start guide</li>\r </ul>\r </div>', 'GPS,Garmin,60 i,Versi Indonesia', 'GPS ,Garmin ,60 i, Versi Indonesia', 'GPS Garmin 60 i (Versi Indonesia)'),
('20080919072722', '20080919070258', '20080919070002', 'Garmin GPSMap 76 Csx i (Versi Indonesia)', 'Bahasa Indonesia User-interface', 'Telah hadir GARMIN 76 CSXi yang telah disempurnakan (software dan hardware), dengan fitur utama Bahasa Indonesia, memudahkan pengguna dan sangat cocok digunakan di wilayah zona region Indonesia. Sangat tangguh untuk digunakan di berbagai kondisi dan tempat apapun.  <br /><br />Dengan bermesin receiver GPS yang ULTRA HIGH-SENSITIVITY, menjadikan 76 CSXi lebih unggul, hebat, dan dapat digunakan dalam kondisi yang paling sulit sekalipun. Waterproof dan Rugged, menyimpan koordinat, membuat track, dan fungsi navigasi lainnya begitu mudah Anda lakukan. Sangat mudah dan cocok untuk digunakan bagi Profesional dan para Hobiest dalam segala bidang pekerjaan lapangan, seperti : surveying gis, oil &amp; gas, perkebuanan, kehutanan, mining, kelautan, dsb.', '20080919072722-Garmin GPSMap 76 Cs i (Versi Indonesia)x.jpg', 0, 0, '4300000', NULL, NULL, 'The GPSMAP 76CSx adds a whole new dimension to the mariner-friendly 76-series navigators: a high-sensitivity GPS receiver, barometric altimeter and electronic compass, microSD&trade; card slot, color TFT screen and turn-by-turn routing &mdash; features that have made the 76CSx and its counterpart, the GPSMAP 76Cx, a mainstay among serious outdoor enthusiasts.\r <h3>Navigate the Great Outdoors</h3>\r <br /><br /><br /><br /><br /><br /><br /><br />Find your way through the wilderness with the GPSMAP 76CSx&#39;s high-sensitivity GPS receiver, which acquires satellite signals quickly and tracks your location in challenging conditions, such as heavy tree cover or deep canyons. You can use the built-in electronic compass to view bearing information even while you&#39;re standing still, and the barometric altimeter tracks changes in pressure to pinpoint your precise altitude. You can even use the altimeter to plot barometric or ambient pressure over time, which can help you keep an eye on changing weather conditions. The 76CSx&#39;s bright, color TFT display makes it easy to distinguish details on the screen in any kind of lighting. And, like the rest of the 76-series, 76CSx is IPX7 waterproof and even floats in water.\r <h3>Expand Your Memory</h3>\r <br /><br /><br /><br /><br /><br /><br /><br />Storing data is easier than ever with the 76CSx&#39;s removable microSD card. This included blank card stores up to 128 megabytes (MB) of optional maps or other data. The card slot is located inside the waterproof battery compartment, so you don&#39;t have to worry about getting it wet. When navigating on the road, the 76CSx provides turn-by-turn directions to your destination. It also accepts optional preloaded microSD cards with MapSource&reg; data for your off-road or marine excursions.  <br /><br />GPSMAP 76CSx: Color navigation and direction for your land and sea adventures', '<table id="playlist" border="0" cellspacing="0">\r <tbody>\r <tr class="title">\r <td colspan="2">\r <h3>Physical &amp; Performance:</h3>\r </td>\r </tr>\r <tr class="odd">\r <td>Unit dimensions, WxHxD:</td>\r <td>2.7" x 6.2" x 1.2" (6.9 x 15.7 x 3.0 cm)</td>\r </tr>\r <tr class="even">\r <td>Display size, WxH:</td>\r <td>1.6" x 2.2" (4.1 x 5.6 cm)</td>\r </tr>\r <tr class="odd">\r <td>Display resolution, WxH:</td>\r <td>160 x 240 pixels</td>\r </tr>\r <tr class="even">\r <td>Display type:</td>\r <td>256 color transflective TFT</td>\r </tr>\r <tr class="odd">\r <td>Weight:</td>\r <td>7.7 oz (218 g) with batteries</td>\r </tr>\r <tr class="even">\r <td>Battery:</td>\r <td>2 AA batteries (not included)</td>\r </tr>\r <tr class="odd">\r <td>Battery life:</td>\r <td>18 hours, typical</td>\r </tr>\r <tr class="even">\r <td>Waterproof:</td>\r <td>yes (IPX7)</td>\r </tr>\r <tr class="odd">\r <td>Floats:</td>\r <td>yes</td>\r </tr>\r <tr class="even">\r <td>High-sensitivity receiver:</td>\r <td>yes</td>\r </tr>\r <tr class="odd">\r <td>Interface:</td>\r <td>serial and USB</td>\r </tr>\r <tr class="even">\r <td>RoHS version available:</td>\r <td>yes</td>\r </tr>\r </tbody>\r </table>\r <table id="playlist" border="0" cellspacing="0">\r <tbody>\r <tr class="title">\r <td colspan="2">\r <h3>Maps &amp; Memory:</h3>\r </td>\r </tr>\r <tr class="even">\r <td>Basemap:</td>\r <td>yes</td>\r </tr>\r <tr class="odd">\r <td>Preloaded maps:</td>\r <td>no</td>\r </tr>\r <tr class="even">\r <td>Ability to add maps:</td>\r <td>yes</td>\r </tr>\r <tr class="odd">\r <td>Built-in memory:</td>\r <td>none (cannot load maps to internal memory)</td>\r </tr>\r <tr class="even">\r <td>Accepts data cards:</td>\r <td>128 microSD&trade; card (included)</td>\r </tr>\r <tr class="odd">\r <td>Waypoints/favorites/locations:</td>\r <td>1000</td>\r </tr>\r <tr class="even">\r <td>Routes:</td>\r <td>50</td>\r </tr>\r <tr class="odd">\r <td>Track log:</td>\r <td>10,000 points; 20 saved tracks</td>\r </tr>\r </tbody>\r </table>\r <table id="playlist" border="0" cellspacing="0">\r <tbody>\r <tr class="title">\r <td colspan="2">\r <h3>Features:</h3>\r </td>\r </tr>\r <tr class="odd">\r <td>Automatic routing (turn by turn routing on roads):</td>\r <td>yes</td>\r </tr>\r <tr class="even">\r <td>Electronic compass:</td>\r <td>yes</td>\r </tr>\r <tr class="odd">\r <td>Barometric altimeter:</td>\r <td>yes</td>\r </tr>\r <tr class="even">\r <td>Geocaching-friendly:</td>\r <td>yes</td>\r </tr>\r <tr class="odd">\r <td>Outdoor GPS games:</td>\r <td>yes</td>\r </tr>\r <tr class="even">\r <td>Hunt/fish calendar:</td>\r <td>yes</td>\r </tr>\r <tr class="odd">\r <td>Sun and moon information:</td>\r <td>yes</td>\r </tr>\r <tr class="even">\r <td>Tide tables:</td>\r <td>yes</td>\r </tr>\r <tr class="odd">\r <td>Area calculation:</td>\r <td>yes</td>\r </tr>\r <tr class="even">\r <td>Custom POIs (ability to add additional points of interest):</td>\r <td>yes</td>\r </tr>\r <tr class="odd">\r <td>Unit-to-unit transfer (shares data wirelessly with similar units):</td>\r <td>no</td>\r </tr>\r <tr class="even">\r <td>Picture viewer:</td>\r <td>no</td>\r </tr>\r </tbody>\r </table>', 'What&#39;s in the Box:<br /><br />&nbsp;&nbsp;&nbsp; * GPSMAP 76CSx<br />&nbsp;&nbsp;&nbsp; * One basemap below:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; o Americas:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Americas Recreational<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; o Europe:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Atlantic Recreational<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; o Australia:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pacific Recreational<br />&nbsp;&nbsp;&nbsp; * 128 MB microSD card<br />&nbsp;&nbsp;&nbsp; * USB cable<br />&nbsp;&nbsp;&nbsp; * MapSource Trip &amp; Waypoint Manager<br />&nbsp;&nbsp;&nbsp; * Wrist strap<br />&nbsp;&nbsp;&nbsp; * Owner&#39;s manual<br />&nbsp;&nbsp;&nbsp; * Quick reference guide', 'Garmin,GPSMap,76 Csx i,Versi Indonesia', 'Garmin, GPSMap, 76 Csx i ,Versi Indonesia', 'Garmin GPSMap 76 Csx i (Versi Indonesia)'),
('20080919073015', '20080919070258', '20080919070002', 'Garmin GPS Colorado 300i (Versi Indonesia)', NULL, NULL, '20080919073015-GPS Garmin Colorado 300i (Versi Indonesia)x.jpg', 0, 0, '6200000', NULL, NULL, 'Gear up and explore the backcountry with the all-terrain Colorado 300. Packed with features, it includes a built-in basemap, high-sensitivity receiver, barometric altimeter, electronic compass, SD&trade; card slot, color display, picture viewer and more. Even exchange tracks, waypoints, routes and geocaches wirelessly between similar units. When the going gets tough, Colorado leads the way.<br />Explore More<br /><br />Colorado 300 comes with a built-in worldwide basemap with shaded relief and accepts SD cards for all your outdoor activities on land or water. Just take your pick from Garmin&rsquo;s preprogrammed SD cards including street maps, topographic maps, coastal charts or inland lake data. The card slot is located inside the waterproof battery compartment, so you don&#39;t have to worry about getting it wet.<br />Share Wirelessly<br /><br />With Colorado 300 you can share your waypoints, tracks, routes and geocaches wirelessly with other Colorado users. Now you can send your favorite hike to your buddy to enjoy or the location of a cache to find. Sharing data is easy. Just select &ldquo;send&rdquo; to transfer your information to other Colorado units.<br />Rock on. Find Fun<br /><br />Intuitive and fun, Colorado features a Rock &lsquo;n Roller&trade; input wheel for easy one-handed operation and supports Geocaching.com GPX files for downloading geocaches and detail straight to your unit. Customize Colorado&#39;s interface based on your favorite activity. Even show off photos of your excursions with its picture viewer. Slim, lightweight and waterproof, Colorado is the perfect companion for all your outdoor pursuits.<br />Keep Your Fix<br /><br />With its high-sensitivity, WAAS-enabled GPS receiver, Colorado 300 locates your position quickly and precisely and maintains its GPS location even in heavy cover and deep canyons. The advantage is clear &mdash; whether you&rsquo;re in deep woods or just near tall buildings and trees, you can count on Colorado to help you find your way when you need it the most.<br />Get Your Bearings<br /><br />Colorado 300 has a built-in electronic compass that provides bearing information even while you&#39;re standing still, and its barometric altimeter tracks changes in pressure to pinpoint your precise altitude. You can even use the altimeter to plot barometric pressure over time, which can help you keep an eye on changing weather conditions.<br /><br />Colorado 300: For adventure in any direction.', '<table id="playlist" border="0" cellspacing="0">\r <tbody>\r <tr>\r <td colspan="2">\r <h3>Physical &amp; Performance:</h3>\r </td>\r </tr>\r <tr>\r <td>Unit dimensions, WxHxD:</td>\r <td>2.4" x 5.5" x 1.4" (6.0 x 13.9 x 3.5 cm)</td>\r </tr>\r <tr>\r <td>Display size, WxH:</td>\r <td>1.53"W x 2.55"H (3.8 x 6.3 cm); 3" diag (7.6 cm)</td>\r </tr>\r <tr>\r <td>Display resolution, WxH:</td>\r <td>240 x 400 pixels</td>\r </tr>\r <tr>\r <td>Display type:</td>\r <td>Transflective color TFT</td>\r </tr>\r <tr>\r <td>Weight:</td>\r <td>7.3 oz (206.9 g) with batteries</td>\r </tr>\r <tr>\r <td>Battery:</td>\r <td>2 AA batteries (not included);  NiMH or Lithium recommended</td>\r </tr>\r <tr>\r <td>Battery life:</td>\r <td>15 hours</td>\r </tr>\r <tr>\r <td>Waterproof:</td>\r <td>yes (IPX7)</td>\r </tr>\r <tr>\r <td>Floats:</td>\r <td>no</td>\r </tr>\r <tr>\r <td>High-sensitivity receiver:</td>\r <td>yes</td>\r </tr>\r <tr>\r <td>Interface:</td>\r <td>USB and NMEA 0183 compatible</td>\r </tr>\r </tbody>\r </table>\r <table id="playlist" border="0" cellspacing="0">\r <tbody>\r <tr>\r <td colspan="2">\r <h3>Maps &amp; Memory:</h3>\r </td>\r </tr>\r <tr>\r <td>Preloaded maps:</td>\r <td>no</td>\r </tr>\r <tr>\r <td>Ability to add maps:</td>\r <td>yes</td>\r </tr>\r <tr>\r <td>Built-in memory:</td>\r <td>384 MB</td>\r </tr>\r <tr>\r <td>Accepts data cards:</td>\r <td>SD<sup>TM</sup> card (not included)</td>\r </tr>\r <tr>\r <td>Waypoints/favorites/locations:</td>\r <td>1000</td>\r </tr>\r <tr>\r <td>Routes:</td>\r <td>50</td>\r </tr>\r <tr>\r <td>Track log:</td>\r <td>10,000 points, 20 saved tracks</td>\r </tr>\r </tbody>\r </table>\r <table id="playlist" border="0" cellspacing="0">\r <tbody>\r <tr>\r <td colspan="2">\r <h3>Features:</h3>\r </td>\r </tr>\r <tr>\r <td>Automatic routing (turn by turn routing on roads):</td>\r <td>yes (with optional mapping for detailed roads)</td>\r </tr>\r <tr>\r <td>Electronic compass:</td>\r <td>yes</td>\r </tr>\r <tr>\r <td>Barometric altimeter:</td>\r <td>yes</td>\r </tr>\r <tr>\r <td>Geocaching-friendly:</td>\r <td>yes (paperless)</td>\r </tr>\r <tr>\r <td>Outdoor GPS games:</td>\r <td>yes</td>\r </tr>\r <tr>\r <td>Hunt/fish calendar:</td>\r <td>yes</td>\r </tr>\r <tr>\r <td>Sun and moon information:</td>\r <td>yes</td>\r </tr>\r <tr>\r <td>Tide tables:</td>\r <td>yes</td>\r </tr>\r <tr>\r <td>Area calculation:</td>\r <td>yes</td>\r </tr>\r <tr>\r <td>Custom POIs (ability to add additional points of interest):</td>\r <td>yes</td>\r </tr>\r <tr>\r <td>Unit-to-unit transfer (shares data wirelessly with similar units):</td>\r <td>yes</td>\r </tr>\r <tr>\r <td>Picture viewer:</td>\r <td>yes</td>\r </tr>\r </tbody>\r </table>', '<h3>What&#39;s in the Box:</h3>\r <ul>\r <li>Colorado 300</li>\r <li>Worldwide basemap with shaded relief</li>\r <li>Carabiner clip</li>\r <li>USB cable</li>\r <li>MapSource Trip &amp; Waypoint Manager</li>\r <li>Owner&#39;s manual on disk</li>\r <li>Quick start guide </li>\r </ul>', 'GPS,Garmin,Colorado 300i,Versi Indonesia', 'GPS, Garmin ,Colorado 300i ,Versi Indonesia', 'GPS Garmin Colorado 300i (Versi Indonesia)'),
('20080919072309', '20080919070258', '20080919070002', 'Garmin GPS eTrex Hi (Versi Indonesia)', 'GPS high-receiver sensitif', 'Perfect for outdoor enthusiasts on a budget, eTrex H comes with a high-sensitivity GPS receiver that locks onto satellite signals quickly and maintains accuracy - even in the toughest conditions. And just like the popular eTrex, this little yellow wonder combines an intuitive interface and a rugged exterior into a lightweight handheld that fits in the palm of your hand.', '20080919072309-Garmin GPS eTre Hi (Versi Indonesia)x.jpg', 0, 0, '1650000', NULL, NULL, '<div id="desc1">\r <h3>Enjoy Clear Reception</h3>\r <br /><br />With its high-sensitivity, <a href="http://www.garmin.com/aboutGPS/waas.html">WAAS</a>-enabled GPS receiver, eTrex H locates your position quickly and precisely and maintains its GPS location even in heavy cover and deep canyons. The advantage is clear &mdash; whether you&rsquo;re in deep woods or just near tall buildings and trees, you can count on eTrex H to help you find your way when you need it the most.\r <h3>Built for Adventure</h3>\r <br /><br />You can operate your eTrex H with just one hand using five buttons, which are located on either side of eTrex H within easy reach of your fingers. Thanks to its bright yellow case, eTrex H is easy to find in your boat or backpack. And, eTrex H is IPX7 waterproof, so it can take an accidental splash or dunk in the water and still continue to perform.\r <h3>Find It and Go</h3>\r <br /><br />With eTrex H, your adventure can last and last &mdash; up to 17 hours on just 2 AA batteries. While you roam, you can store up to 500 waypoints in memory for easy retrieval. Just enter the point you want to go to, and eTrex H points you to your destination (no street or terrain maps). To get home, use Garmin&#39;s exclusive TrackBack&reg; feature to reverse your track log and help you navigate back to your starting point.  <br /><br />eTrex H: Lock onto the great outdoors.</div>', '-', '<div id="desc3" style="display: block;">\r <h3>What&#39;s in the Box:</h3>\r <ul>\r <li>eTrex H</li>\r <li>Wrist strap</li>\r <li>Owner&#39;s manual</li>\r <li>Quick start guide</li>\r </ul>\r </div>', 'Garmin,GPS,eTrex Hi,Versi Indonesia', 'Garmin ,GPS ,eTrex Hi,Versi Indonesia', 'Garmin GPS eTrex Hi (Versi Indonesia)'),
('20080919073238', '20080919070258', '20080919070002', 'Garmin GPSMAP 420si', NULL, NULL, '20081022085000-gpsmap_420sx.jpg', 0, 0, '6500000', NULL, NULL, 'The GPSMAP 420 is a sleek, space-saving chartplotter that features an ultra-bright 4&rdquo; QVGA display; built-in, satellite-enhanced worldwide basemap and simplified user operation. The 420 also has an SD&trade; card slot so you can add additional features with the optional BlueChart&reg; g2 Vision card.\r <h3>View Satellite-enhanced Maps</h3>\r <br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br />The 420&rsquo;s basemap contains worldwide satellite images in place of more traditional maps, and its SD card slot makes it easy to add additional maps without connecting to a computer. With fewer keys and a simple, intuitive interface, the 420 makes navigating your way through the water easy.\r <h3>Add Sonar</h3>\r <br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br />Where applicable, the sounder version, GPSMAP 420s, comes with a powerful dual frequency transducer that clearly paints depth contours, fish targets and structure whether you&rsquo;re fishing in freshwater or off the coast. The 420 and 420s are also CANet&trade; compatible, so you can share sounder data with another chartplotter on your boat.\r <h3>Add Extras with BlueChart g2 Vision</h3>\r <br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br />With the optional BlueChart g2 Vision SD card, you can add a whole new dimension of exceptional features to your chartplotter.\r <ul>\r <li>3D mariner&rsquo;s eye view or fish eye view provides you with a 3-dimensional perspective of map information, both above the waterline and below it.</li>\r <li>High-resolution satellite images and aerial photographs help you orient yourself in unfamiliar areas.</li>\r <li>Auto Guidance technology searches through all relevant charts to create a route you can actually follow &mdash; one that avoids obstacles, shallow water, buoys and other obstructions.</li>\r </ul>', '<table id="playlist" border="0" cellspacing="0">\r <tbody>\r <tr class="title">\r <td colspan="2">\r <h3>Physical &amp; Performance:</h3>\r </td>\r </tr>\r <tr class="odd">\r <td>Unit dimensions, WxHxD:</td>\r <td>5.7" x 5.0" x 2.7" (14.5 x 12.7 x  6.9 cm)</td>\r </tr>\r <tr class="even">\r <td>Display size, WxH:</td>\r <td>2.4" x 3.2", 4.0" diagonal (6.1 x 8.1 cm, 10.2 cm diagonal)</td>\r </tr>\r <tr class="odd">\r <td>Display resolution, WxH:</td>\r <td>240 x 320 pixels</td>\r </tr>\r <tr class="even">\r <td>Display type:</td>\r <td>QVGA display</td>\r </tr>\r <tr class="odd">\r <td>Weight:</td>\r <td>1.3 lb (590 g)</td>\r </tr>\r <tr class="even">\r <td>Battery:</td>\r <td>none</td>\r </tr>\r <tr class="odd">\r <td>Battery life:</td>\r <td>none</td>\r </tr>\r <tr class="even">\r <td>Waterproof:</td>\r <td>yes (IPX7)</td>\r </tr>\r <tr class="odd">\r <td>Antenna:</td>\r <td>internal</td>\r </tr>\r <tr class="even">\r <td>NMEA input/output:</td>\r <td>NMEA 0183</td>\r </tr>\r </tbody>\r </table>\r <table id="playlist" border="0" cellspacing="0">\r <tbody>\r <tr class="title">\r <td colspan="2">\r <h3>Maps &amp; Memory:</h3>\r </td>\r </tr>\r <tr class="odd">\r <td>Basemap:</td>\r <td>yes</td>\r </tr>\r <tr class="even">\r <td>Preloaded maps:</td>\r <td>no</td>\r </tr>\r <tr class="odd">\r <td>Ability to add maps:</td>\r <td>yes</td>\r </tr>\r <tr class="even">\r <td>Built-in memory:</td>\r <td>none (cannot load maps to internal memory)</td>\r </tr>\r <tr class="odd">\r <td>Accepts data cards:</td>\r <td>SD&trade; card slot</td>\r </tr>\r <tr class="even">\r <td>Waypoints/favorites/locations:</td>\r <td>1,500</td>\r </tr>\r <tr class="odd">\r <td>Routes:</td>\r <td>20</td>\r </tr>\r <tr class="even">\r <td>Track log:</td>\r <td>10,000 points; 20 saved tracks</td>\r </tr>\r </tbody>\r </table>\r <table id="playlist" border="0" cellspacing="0">\r <tbody>\r <tr class="title">\r <td colspan="2">\r <h3>Features:</h3>\r </td